def read_vocabulary_list(csv_path: Path) -> List[Dict[str, str]]:
    """Read vocabulary list CSV."""
    words = []
    with open(csv_path, 'r', encoding='utf-8') as f:
        reader = csv.DictReader(f)
        for row in reader:
            words.append({
                'word': row['word'].strip().lower(),
                'level': row['assigned_level'].strip()
            })
    return words

def get_content(word: str) -> Dict:
    """Get content for a word from the database."""
    if word in VOCAB_CONTENT:
        return VOCAB_CONTENT[word]
    return None

def main():
    base_dir = Path(__file__).parent.parent
    data_dir = base_dir / 'data'
    vocab_list_path = data_dir / 'vocabularyList.csv'
    
    print("Reading vocabulary list...")
    words = read_vocabulary_list(vocab_list_path)
    print(f"Total words: {len(words)}")
    
    # Count words with content
    words_with_content = sum(1 for w in words if w['word'] in VOCAB_CONTENT)
    print(f"Words with content defined: {words_with_content}")
    
    # Write vocabulary content
    output_path = data_dir / 'vocabulary_content_new.csv'
    fieldnames = ['word', 'meaning', 'synonym1', 'synonym2', 'antonym1', 'antonym2', 'example_sentence']
    
    with open(output_path, 'w', encoding='utf-8', newline='') as f:
        writer = csv.DictWriter(f, fieldnames=fieldnames)
        writer.writeheader()
        
        for word_data in words:
            word = word_data['word']
            content = get_content(word)
            
            if content:
                writer.writerow({
                    'word': word,
                    'meaning': content['meaning'],
                    'synonym1': content['synonyms'][0] if content['synonyms'] else '',
                    'synonym2': content['synonyms'][1] if len(content['synonyms']) > 1 else '',
                    'antonym1': content['antonyms'][0] if content['antonyms'] else '',
                    'antonym2': content['antonyms'][1] if len(content['antonyms']) > 1 else '',
                    'example_sentence': content['example']
                })
            else:
                # Empty row for words without content
                writer.writerow({
                    'word': word,
                    'meaning': '',
                    'synonym1': '',
                    'synonym2': '',
                    'antonym1': '',
                    'antonym2': '',
                    'example_sentence': ''
                })
    
    print(f"Written to {output_path}")

if __name__ == '__main__':
    main()
